import { useEffect, useState } from "react";
// import { useParams } from "react-router-dom";

function Detail() {
//   const { id } = useParams();

  const [loading, setLoading] = useState(true);
  const [movie, setMovie] = useState([]);

  const getMovie = async () => {
    const json = await (
      await fetch(`https://yts.mx/api/v2/movie_details.json?movie_id=12345`)
    ).json();

    setMovie(json.data.movie);
    setLoading(false);
  };

  useEffect(() => {
    getMovie();
  }, []);

  return (
    <div>
      {loading ? (
        <h1>Loading...</h1>
      ) : (
        <div>
          {movie.map((detail) => (
            <h1>detail.id</h1>
          ))}
        </div>
      )}
    </div>
  );
}

export default Detail;
