function App() {
  const [Loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(
      `https://yts.mx/api/v2/list_movies.json?minimum_rating=9&sort_by=year`
    ).then(
		setLoading(false)
	);

    // return () => {
